shared/schema.ts.content:
import { pgTable, text, serial, integer, boolean, timestamp, json } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

export const playerStats = pgTable("player_stats", {
  id: serial("id").primaryKey(),
  playerName: text("player_name").notNull().unique(),
  gamesPlayed: integer("games_played").notNull().default(0),
  gamesWon: integer("games_won").notNull().default(0),
  totalCardsPlayed: integer("total_cards_played").notNull().default(0),
  createdAt: timestamp("created_at").notNull().defaultNow(),
  updatedAt: timestamp("updated_at").notNull().defaultNow(),
});

export const matchHistory = pgTable("match_history", {
  id: serial("id").primaryKey(),
  roomId: text("room_id").notNull(),
  players: json("players").$type<Array<{ id: string; name: string; finalCardCount: number }>>().notNull(),
  winnerId: text("winner_id").notNull(),
  winnerName: text("winner_name").notNull(),
  duration: integer("duration").notNull(),
  createdAt: timestamp("created_at").notNull().defaultNow(),
});

export const insertPlayerStatsSchema = createInsertSchema(playerStats);
export const insertMatchHistorySchema = createInsertSchema(matchHistory);

export type InsertPlayerStats = z.infer<typeof insertPlayerStatsSchema>;
export type PlayerStats = typeof playerStats.$inferSelect;
export type InsertMatchHistory = z.infer<typeof insertMatchHistorySchema>;
export type MatchHistory = typeof matchHistory.$inferSelect;

server/statsService.ts.content:
import { db } from "./db";
import { playerStats, matchHistory, type InsertMatchHistory } from "@shared/schema";
import { eq, sql } from "drizzle-orm";

export interface MatchResult {
  roomId: string;
  players: Array<{
    id: string;
    name: string;
    finalCardCount: number;
  }>;
  winnerId: string;
  winnerName: string;
  duration: number;
}

export async function saveMatchResult(result: MatchResult) {
  try {
    // Save match to history
    await db.insert(matchHistory).values({
      roomId: result.roomId,
      players: result.players,
      winnerId: result.winnerId,
      winnerName: result.winnerName,
      duration: result.duration,
    });

    // Update player stats for all players
    for (const player of result.players) {
      const existingStats = await db.select()
        .from(playerStats)
        .where(eq(playerStats.playerName, player.name))
        .limit(1);

      if (existingStats.length > 0) {
        // Update existing stats
        await db.update(playerStats)
          .set({
            gamesPlayed: sql`${playerStats.gamesPlayed} + 1`,
            gamesWon: player.id === result.winnerId 
              ? sql`${playerStats.gamesWon} + 1` 
              : playerStats.gamesWon,
            updatedAt: new Date(),
          })
          .where(eq(playerStats.playerName, player.name));
      } else {
        // Create new stats
        await db.insert(playerStats).values({
          playerName: player.name,
          gamesPlayed: 1,
          gamesWon: player.id === result.winnerId ? 1 : 0,
          totalCardsPlayed: 0,
        });
      }
    }

    console.log(`Match result saved for room ${result.roomId}`);
  } catch (error) {
    console.error('Error saving match result:', error);
  }
}

export async function updateCardsPlayed(playerName: string, cardsCount: number) {
  try {
    const existingStats = await db.select()
      .from(playerStats)
      .where(eq(playerStats.playerName, playerName))
      .limit(1);

    if (existingStats.length > 0) {
      await db.update(playerStats)
        .set({
          totalCardsPlayed: sql`${playerStats.totalCardsPlayed} + ${cardsCount}`,
          updatedAt: new Date(),
        })
        .where(eq(playerStats.playerName, playerName));
    }
  } catch (error) {
    console.error('Error updating cards played:', error);
  }
}

